INSERT INTO `notification` (`id`, `idk`, `title`, `detail`, `created_at`) VALUES
(2, 'KhoaCNTT', 'Khoa CNTT - Thông báo về việc chuẩn bị nộp và bảo vệ ĐATN cho Khóa 13T', '<p>Khoa CNTT th&ocirc;ng b&aacute;o cho c&aacute;c sinh vi&ecirc;n kh&oacute;a 13T &nbsp;một số nội dung cho đợt bảo vệ ĐATN sắp đến:</p>\r\n\r\n<h2>1. Chuẩn bị b&aacute;o c&aacute;o ĐATN</h2>\r\n\r\n<p>&sect;&nbsp;&nbsp;02 SV l&agrave;m chung một đề t&agrave;i c&oacute; thể l&agrave;m chung 01 b&aacute;o c&aacute;o (được GVHD đồng &yacute;). Cần ch&uacute; &yacute; l&agrave;m r&otilde; nội dung l&agrave;m việc của từng sinh vi&ecirc;n trong ĐATN,</p>\r\n\r\n<p>&sect;&nbsp;&nbsp;B&aacute;o c&aacute;o ĐATN được tr&igrave;nh b&agrave;y theo mẫu qui định của Nh&agrave; trường (<a href=\"http://www.dut.udn.vn/Files/k.cntt/files/DHBK%20-%20Quyet%20dinh%20%26%20Qui%20dinh%20Do%20An%20Tot%20Nghiep%2003-2017(1).pdf\">xem file đ&iacute;nh k&egrave;m</a>).</p>\r\n\r\n<p>&sect;&nbsp;&nbsp;Phiếu duyệt ĐATN theo mẫu của Khoa được đặt ngay trước phần Mục lục,</p>\r\n\r\n<p>&sect;&nbsp;&nbsp;Phần T&oacute;m tắt đồ &aacute;n (tiếng Việt, c&oacute; thể bổ sung tiếng Anh) được đặt ngay trước phần T&agrave;i liệu tham khảo của b&aacute;o c&aacute;o,</p>\r\n\r\n<p>&sect;&nbsp;&nbsp;Tất cả c&aacute;c sinh vi&ecirc;n phải nộp tệp tin MS Word b&aacute;o c&aacute;o đồ &aacute;n v&agrave;o địa chỉ email:&nbsp;<em><a href=\"mailto:cnttdhdn@gmail.com\" target=\"_blank\">cnttdhdn@gmail.com</a></em>&nbsp;trước ng&agrave;y 30/05/2018.</p>\r\n\r\n<p>&sect;&nbsp;&nbsp;Đặt t&ecirc;n tệp tin (Tiếng Việt kh&ocirc;ng dấu):&nbsp;<em>Lopxxxx_HoVaTen_TenDeTai.docx.</em>&nbsp;</p>\r\n\r\n<h2>2. Duyệt đồ &aacute;n tốt nghiệp (7h ng&agrave;y 21/05/2018)</h2>\r\n\r\n<p>&sect;&nbsp;&nbsp;Địa điểm duyệt ĐATN: C&aacute;c ph&ograve;ng m&aacute;y t&iacute;nh Khoa CNTT &ndash; Trường ĐHBK</p>\r\n\r\n<p>&sect;&nbsp;&nbsp;Sinh vi&ecirc;n cần chuẩn bị cho buổi duyệt ĐATN:</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;M&aacute;y t&iacute;nh c&oacute; sẵn chương tr&igrave;nh nguồn, c&ocirc;ng cụ bi&ecirc;n dịch,</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;02 Bản in b&aacute;o c&aacute;o Đồ &aacute;n tốt nghiệp c&oacute; chữ k&yacute; của GVHD (01 cho Khoa, 01 cho TTHL)</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;02 đĩa CD chứa: tập tin đồ &aacute;n (dạng *.pdf v&agrave; dạng *.doc), chương tr&igrave;nh nguồn, c&aacute;c c&ocirc;ng cụ hỗ trợ, hướng dẫn sử dụng, Slide.</p>\r\n\r\n<p>&sect;&nbsp;&nbsp;Sinh vi&ecirc;n tr&igrave;nh b&agrave;y chương tr&igrave;nh, b&aacute;o c&aacute;o v&agrave; trả lời c&aacute;c c&acirc;u hỏi của GV duyệt,</p>\r\n\r\n<p>&sect;&nbsp;&nbsp;GV duyệt đ&aacute;nh gi&aacute; kết quả chương tr&igrave;nh theo nội dung trong phiếu duyệt,</p>\r\n\r\n<p>&sect;&nbsp;&nbsp;Sinh vi&ecirc;n nộp quyển b&aacute;o c&aacute;o ĐATN, đĩa CD chương tr&igrave;nh cho GV duyệt,</p>\r\n\r\n<p>&sect;&nbsp;&nbsp;Sinh vi&ecirc;n k&yacute; t&ecirc;n v&agrave;o danh s&aacute;ch để x&aacute;c nhận đ&atilde; nộp Đồ &aacute;n cho GV duyệt</p>\r\n\r\n<h2>3. Bảo vệ ĐATN (Ng&agrave;y 04-05/06/2018)</h2>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Thứ tự bảo vệ ĐATN theo danh s&aacute;ch được ni&ecirc;m yết trước ph&ograve;ng&nbsp; bảo vệ,</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Sinh vi&ecirc;n tr&igrave;nh b&agrave;y Slide nội dung ĐATN trong thời gian tối đa 15 ph&uacute;t,</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Người phản biện đọc nhận x&eacute;t, đặt c&aacute;c c&acirc;u hỏi li&ecirc;n quan đến ĐATN,</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Sinh vi&ecirc;n trả lời c&aacute;c c&acirc;u hỏi của người phản biện v&agrave; c&aacute;c th&agrave;nh vi&ecirc;n Hội đồng,</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Đ&aacute;nh gi&aacute; kết quả bảo vệ đồ &aacute;n tốt nghiệp dựa tr&ecirc;n dựa tr&ecirc;n: 1) Kết quả thực hiện đề t&agrave;i; 2) Tr&igrave;nh b&agrave;y b&aacute;o c&aacute;o của sinh vi&ecirc;n; 3) Trả lời c&aacute;c c&acirc;u hỏi của Hội đồng bảo vệ; 4) T&iacute;nh khoa học v&agrave; khả năng ứng dụng của đề t&agrave;i,</p>\r\n\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Điểm bảo vệ t&iacute;nh theo trung b&igrave;nh cộng điểm của c&aacute;c th&agrave;nh vi&ecirc;n Hội đồng, người hướng dẫn v&agrave; người phản biện, l&agrave;m tr&ograve;n 01 chữ số thập ph&acirc;n.</p>\r\n\r\n<p><a href=\"http://www.dut.udn.vn/Files/k.cntt/files/DHBK%20-%20Quyet%20dinh%20%26%20Qui%20dinh%20Do%20An%20Tot%20Nghiep%2003-2017.pdf\">* Quy định đồ &aacute;n tốt nghiệp của Trường Đại học B&aacute;ch khoa, ĐNĐN.</a></p>\r\n\r\n<p>&nbsp;<strong>Khoa CNTT</strong></p>\r\n', '2018-05-21 08:23:07'),
(3, 'KhoaCNTT', '[MGM] Calling all IT students!', '<p>Do you have plans for the summer? Do you want to practice and learn new coding skills?&nbsp; Do you want to experience working for a great international IT company in Da Nang?</p>\r\n\r\n<p>Apply to mgm&rsquo;s international internship- 2018!</p>\r\n\r\n<p>We offer:</p>\r\n\r\n<ul>\r\n	<li>a chance to experience working in an international company</li>\r\n	<li>free English for IT classes</li>\r\n	<li>a senior developer mentor at mgm</li>\r\n	<li>company dinners and activities</li>\r\n	<li>weekly stipend</li>\r\n</ul>\r\n\r\n<p>We are looking for motivated students who are passionate about software development and who strive to work in an international tech company.</p>\r\n\r\n<p>Follow the link&nbsp;<a href=\"http://bit.ly/mgm2018dn\">http://bit.ly/mgm2018dn</a>&nbsp;to apply.</p>\r\n', '2018-05-21 23:56:31'),
(5, 'common', 'Th&ocirc;ng b&aacute;o tuyển dụng của C&ocirc;ng ty Cổ phần Cao su Đ&agrave; Nẵng', '&lt;p&gt;C&amp;ocirc;ng ty Cổ phần Cao su Đ&amp;agrave; Nẵng tuyển dụng Kỹ sư ng&amp;agrave;nh Kỹ thuật H&amp;oacute;a học, chuy&amp;ecirc;n ng&amp;agrave;nh Polymer&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Số lượng:&lt;/strong&gt;&amp;nbsp;10 kỹ sư&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Địa điểm nộp hồ sơ:&lt;/strong&gt;&amp;nbsp;L&amp;ocirc; G, Tạ Quang Bửu, phường H&amp;ograve;a Hiệp Bắc, quận Li&amp;ecirc;n Chiểu, TP. Đ&amp;agrave; Nẵng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Thời hạn cuối nhận hồ sơ:&lt;/strong&gt;&amp;nbsp;ng&amp;agrave;y 25 th&amp;aacute;ng 10 năm 2018&lt;/p&gt;\r\n', '2018-05-22 01:39:01');

